import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
const AddCategoryForm = () => {
  const { updateCatId, categoryName, elementState, isAdd, isEdit } =
    useSelector((state) => state.categories);
  const params = useParams();
  const [data, setData] = useState({
    categoryName,
    elementState,
    _id: updateCatId || params.id,
  });

  const { setForm, addItem, UpdateItem } = useAppContext();

  return (
    <form
      className="editForm"
      onSubmit={(e) => {
        if (isAdd) {
          return addItem(e, data);
        } else {
          return UpdateItem(e, data);
        }
      }}
      encType="multipart/form-data"
    >
      <h3>{isEdit ? "Edit Category" : "Add Category"}</h3>
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <FormRow
        type="text"
        name="Category Name"
        value={data.categoryName}
        handleChange={(e) => setData({ ...data, categoryName: e.target.value })}
      />
      <FormRow
        type="file"
        name="Category Name"
        value={data.image}
        handleChange={(e) => setData({ ...data, image: e.target.value })}
      />
      <h5>State</h5>
      <FormRow
        type="checkbox"
        name="Active"
        value={data.elementState}
        checked={data.elementState}
        handleChange={(e) =>
          setData({ ...data, elementState: !data.elementState })
        }
      />

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </div>
    </form>
  );
};

export default AddCategoryForm;
