  labelText,
  name,
  value,
  handleChange,
  list,
  x,
  selected,
}) => {
  let s = (itemValue) => {
    if (x === "categories") return itemValue.categoryName;
    if (x === "sizes") return itemValue.sizeName;
    if (x === "colors") return itemValue.colorName;
  };
  return (
    <div className="form-row">
      <label htmlFor={name} className="form-label">
        {labelText || name}
      </label>
      <select
        name={name}
        value={value}
        onClick={handleChange}
        className="form-select"
      >
        <option>select</option>
        {list.map((itemValue, index) => {
          return (
            <option
              // style={{
