import { useDispatch, useSelector } from "react-redux";
import React, { useContext } from "react";
import { useEffect } from "react";
import {
  GET_PRODUCTS,
  UPDATE_PRODUCT,
  DELETE_PRODUCT,
  RESET_PRO,
} from ".././rtk/slices/productsSlice";
import {
  GET_CATEGORIES,
  DELETE_CATEGORY,
  DELETE_RESET_CAT,
} from ".././rtk/slices/categoriesSlice";

import {
  CLEAR_ALERT,
  DISPLAY_ALERT,
  REGISTER_SUCCESS,
} from "../rtk/slices/authSlice";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  GET_COLORS,
  DELETE_RESET_COLOR,
  DELETE_COLOR,
} from ".././rtk/slices/colorsSlice";
import {
  GET_SIZES,
  DELETE_SIZE,
  DELETE_RESET_SIZE,
} from ".././rtk/slices/sizesSlice";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { updatePro, deletedPro } = useSelector((state) => state.products);

  const { deletedCat } = useSelector((state) => state.categories);
  const { deletedColor } = useSelector((state) => state.colors);
  const { deletedSize } = useSelector((state) => state.sizes);
  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2000);
  };

  const displayAlert = (msg) => {
    dispatch(DISPLAY_ALERT({ alertText: msg }));
    clearAlert();
  };
  const successRegister = (user) => {
    dispatch(REGISTER_SUCCESS({ user: user }));
    clearAlert();

    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };

  const getAllItems = async () => {
    const res = await axios.get("/dashboard");
    console.log(res.data.products);
    dispatch(GET_PRODUCTS(res.data.products));
    dispatch(GET_CATEGORIES(res.data.categories));
    dispatch(GET_COLORS(res.data.colors));
    dispatch(GET_SIZES(res.data.sizes));
  };
  const UpdateItem = async (data) => {
    try {
      await axios.post(`/dashboard/MangeProducts/Update/${data.id}`, data);
      dispatch(UPDATE_PRODUCT(data));
      navigate("/dashboard/MangeProducts");
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  const deleteItem = async (id) => {
    const url = window.location.pathname;
    if (url.includes("/MangeProducts")) {
      await axios.delete(`/dashboard/MangeProducts/Delete/${id}`);
      dispatch(DELETE_PRODUCT(id));
    }
    if (url.includes("/MangeCategories")) {
      await axios.delete(`/dashboard/MangeCategories/Delete/${id}`);

      dispatch(DELETE_CATEGORY(id));
    }
    if (url.includes("/MangeSizes")) {
      await axios.delete(`/dashboard/MangeSizes/Delete/${id}`);

      dispatch(DELETE_SIZE(id));
    }
    if (url.includes("/MangeColors")) {
      await axios.delete(`/dashboard/MangeColors/Delete/${id}`);

      dispatch(DELETE_COLOR(id));
    }
  };
  const reset = () => {
    if (deletedPro) dispatch(RESET_PRO());
    if (deletedCat) dispatch(DELETE_RESET_CAT());
    if (deletedColor) dispatch(DELETE_RESET_COLOR());
    if (deletedSize) dispatch(DELETE_RESET_SIZE());
    if (updatePro) dispatch(RESET_PRO());
  };
  useEffect(() => {
    getAllItems();
    // (async () => {
    //   const res = await axios.get("/dashboard");
    //   console.log(res.data.products);
    //   dispatch(GET_PRODUCTS(res.data.products));
    //   dispatch(GET_CATEGORIES(res.data.categories));
    //   dispatch(GET_COLORS(res.data.colors));
    //   dispatch(GET_SIZES(res.data.sizes));
    // })();

    // if (deletedPro) dispatch(DELETE_RESET_PRO());
    // if (deletedCat) dispatch(DELETE_RESET_CAT());
    // if (deletedColor) dispatch(DELETE_RESET_COLOR());
    // if (deletedSize) dispatch(DELETE_RESET_SIZE());
    reset();
  }, [deletedCat, deletedPro, deletedColor, deletedSize, updatePro]);
  return (
    <AppContext.Provider
      value={{
        displayAlert,
        successRegister,
        getAllItems,
        deleteItem,
        UpdateItem,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
