import "./App.css";
import React, { useState, useEffect } from "react";
// import AppNavbar from "./component/AppNavbar";
import { Route, Routes, Navigate } from "react-router-dom";

import Home from "./component/Home/Home";
import Dashboard from "./component/Dashboard/Dashboard";
import Cart from "./component/Home/Cart";
import CheckOut from "./component/Home/CheckOut";
import UpdateProduct from "./component/update/UpdateProduct";
import UpdateCategory from "./component/update/UpdateCategory";
import UpdateSize from "./component/update/UpdateSize";
import UpdateColor from "./component/update/UpdateColor";
import RegisterScreen from "./component/registerScreen/RegisterScreen";
import LoginScreen from "./component/registerScreen/LoginScreen";
import UserContext from "./context/userContext";
import { AppProvider } from "./context/appContext";
import { useDispatch, useSelector } from "react-redux";
import { CLEAR_ALERT } from "./rtk/slices/authSlice";
import ProductsTable from "./component/Dashboard/ProductsTable";
import ProDetailes from "./component/Home/ProDetailes";
import ProByCategory from "./component/Home/ProByCategory";
import ProtectedRoute from "./component/ProtectedRoute";
import { useAppContext } from "../src/context/appContext";

function App() {
  const dispatch = useDispatch();
  const { setForm } = useAppContext();

  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2500);
  };

  const [user, setUser] = useState(null);
  // useEffect(() => {
  //   setUser(authApi.getUser() || null);
  // }, []);

  return (
    <UserContext.Provider value={{ user, setUser }}>
      <AppProvider>
        <div className="App" onClick={setForm(false)}>
          <Routes>
            <Route
              path="/dashboard"
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              }
            >
              <Route path="MangeProducts" element={<Dashboard />} />

              <Route path="MangeCategories" element={<Dashboard />} />
              <Route path="MangeColors" element={<Dashboard />} />
              <Route path="MangeSizes" element={<Dashboard />} />
            </Route>
            {/* <Route path="/" element={<Navigate to="/home" />} /> */}
            <Route path="/" element={<Home />} />
            <Route path="/register" element={<RegisterScreen />} />
            <Route path="/login" element={<LoginScreen />} />
            <Route path="/productDetailes/:id" element={<ProDetailes />} />
            <Route path="/category/:name" element={<ProByCategory />} />
            <Route path="/cart" element={<Cart />}></Route>
            <Route path="/check-out" element={<CheckOut />} />
          </Routes>
        </div>
      </AppProvider>
    </UserContext.Provider>
  );
}

export default App;
