import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import UserContext from "../../context/userContext";
import React, { useContext, useState, useEffect } from "react";
import { SET_USER_lOGOUT } from "../../rtk/slices/authSlice";
import authApi from "../../api/auth";
import Wrapper from "../../Wrapper/AppNavbar";

function AppNavbar() {
  const { user, setUser } = useContext(UserContext);
  const { showAlert, alertType, alertText, auth } = useSelector(
    (state) => state.auth
  );
  const dispatch = useDispatch();
  const state = useSelector((state) => state.cart);
  let tot = 0;
  state.forEach((el) => {
    tot += el.qnt;
  });
  const handleLogOut = () => {
    setUser(null);
    authApi.deleteToken();
    dispatch(SET_USER_lOGOUT());
  };
  return (
    <>
      {/* <Wrapper> */}{" "}
      <nav id="navigation">
        <div class="container">
          <div id="responsive-nav">
            <ul class="main-nav nav navbar-nav" style={{ display: "block" }}>
              <li class="active">
                <a href="/">Home</a>
              </li>
              <li>
                <Link className="nav-link" to="/dashboard">
                  <a href="/dashboard">Dashboard</a>
                </Link>
              </li>
              <li>
                <a href="#">Categories</a>
              </li>
              <li>
                <Link className="nav-link" to="/login">
                  <a href="/login">Login</a>
                </Link>
              </li>
              <li>
                <Link className="nav-link" to="/register">
                  <a href="/register">Register</a>
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      {/* </Wrapper> */}
    </>
  );
}
export default AppNavbar;
