import { createSlice } from "@reduxjs/toolkit";
const productsSlice = createSlice({
  name: "productsSlice",
  initialState: [],
  reducers: {
    addToCart: (state, action) => {
      return (state = action.paylaod);
    },
  },
});

export const { addToCart } = productsSlice.actions;
export default productsSlice.reducer;
