import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";

const AddColorForm = ({ setForm }) => {
  const [data, setData] = useState({
    colorName: "",
  });

  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeCategories/addColor", data);

      console.log(res.data.color);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Color Name</label>
        <input
          type="text"
          className="form-control"
          value={data.colorName}
          onChange={(e) => setData({ ...data, colorName: e.target.value })}
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Color
        </button>
      </div>
    </form>
  );
};

export default AddColorForm;
