import { createSlice } from "@reduxjs/toolkit";

const categoriesSlice = createSlice({
  name: "categoriesSlice",
  initialState: { categories: [] },
  reducers: {
    GET_CATEGORIES: (state, action) => {
      state.categories = action.payload;
    },
    ADD_CATEGORY: (state, action) => {
      state.categories.push(...action.payload);
    },
    DELETE_CATEGORY: (state, action) => {
      state.deletedCat = true;
      state.products.filter((el) => el._id !== action.payload);
    },
    DELETE_RESET: (state, action) => {
      state.deletedCat = false;
    },
  },
});

export const { GET_CATEGORIES, ADD_CATEGORY, DELETE_CATEGORY, DELETE_RESET } =
  categoriesSlice.actions;
export default categoriesSlice.reducer;
