import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";
import "./AddProduct.css";
import Form from "react-bootstrap/Form";
import { ADD_PRODUCT } from "../rtk/slices/productsSlice";
import { insertCategory } from "../rtk/slices/categoriesSlice";
import { insertSize } from "../rtk/slices/sizesSlice";
import { insertColor } from "../rtk/slices/colorsSlice";
import { useNavigate } from "react-router-dom";
import CloseButton from "react-bootstrap/CloseButton";
import ReactDOM from "react-dom";
import React, { useContext, useState, useRef } from "react";
import axios from "axios";
import Modal from "./modal/Modal";
import "./update/UpdateCart.css";
function AddProduct({ formState, setForm }) {
  const dispatch = useDispatch();
  const [data, setData] = useState({
    name: "",
    description: "",
    categories: "",
    price: "",
    image: [],
  });
  const handleImage = (e) => {
    const image = e.target.files[0].name;
    // const array = image.forEach((element) => {
    //   array.push(element.name);
    // });

    console.log(image);
    // console.log(array);
    setData({ ...data, image: image });
  };
  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeProducts/addProduct", data);

      console.log(res.data.product);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };

  return (
    formState && (
      <>
        <Modal>
          {" "}
          {window.location.pathname.includes("/dashboard/MangeProducts") && (
            <form onSubmit={fetchData} encType="multipart/form-data">
              <span className="delete-section">
                <CloseButton
                  onClick={() => {
                    setForm(false);
                  }}
                />
              </span>

              <div className="mb-3 text-start">
                <label>Name</label>
                <input
                  type="text"
                  className="form-control"
                  value={data.fname}
                  onChange={(e) => setData({ ...data, name: e.target.value })}
                  //  ref={name}
                />
              </div>
              <div className="mb-3 text-start">
                <label>Description</label>
                <input
                  type="text"
                  className="form-control"
                  value={data.description}
                  onChange={(e) =>
                    setData({ ...data, description: e.target.value })
                  }
                  // ref={description}
                />
              </div>
              <div className="mb-3 text-start">
                <label>Categories</label>
                <input
                  type="text"
                  className="form-control"
                  value={data.categories}
                  onChange={(e) =>
                    setData({ ...data, categories: e.target.value })
                  }
                  //  ref={categories}
                />
              </div>
              <div className="mb-3 text-start">
                <label>Price</label>
                <input
                  type="text"
                  className="form-control"
                  value={data.price}
                  onChange={(e) => setData({ ...data, price: e.target.value })}
                  // ref={price}
                />
              </div>
              <div className="mb-3 text-start">
                <label>Image</label>
                <input
                  multiple
                  type="file"
                  className="form-control"
                  onChange={(e) => handleImage(e)}
                  // ref={image}
                />
              </div>
              <div className="d-grid">
                <button type="submit" className="btn btn-primary">
                  Add Product
                </button>
              </div>
            </form>
          )}
        </Modal>
      </>
    )
  );
}
export default AddProduct;
