import AppNavbar from "../secrions/AppNavbar";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { useAppContext } from "../../context/appContext";
import React, { useRef } from "react";
import axios from "axios";
import Loading from "../Loading";
import Alert from "../Alert";
const ProDetailes = () => {
  const { loadingPro } = useSelector((state) => state.products);
  const { toCart } = useAppContext();
  const params = useParams();
  const [product, setValue] = useState("");
  const [list_Size, set_List_Size] = useState("");
  const [list_Color, set_List_Color] = useState("");
  const [Qnt, set_Qnt] = useState("");
  const [Cart, setCart] = useState("");
  const x = useRef(true);

  useEffect(() => {
    if (x.current) {
      x.current = false;

      (async () => {
        const res = await axios.get(`/productDetailes/${params.id}`);
        setValue(res.data.product);
      })();
    }
  }, [params.id]);
  const setSize = (e) => {
    if (e === "0") set_List_Size("");
    else {
      set_List_Size({
        ...list_Size,
        selectedSize: e,
      });
      setCart({
        productId: { ...product },
        ...Cart,
        quantity: 1,
        selectedSize: e,
      });
      setValue({ ...product, selectedSize: e, quantity: quantity });
    }
  };
  const setColor = (e) => {
    if (e === "0") set_List_Color("");
    else {
      set_List_Color({
        ...list_Color,

        selectedColor: e,
      });
      setCart({
        productId: { ...product },
        ...Cart,
        quantity: 1,
        selectedColor: e,
      });
      setValue({ ...product, selectedColor: e, quantity: quantity });
    }
  };
  const [quantity, setQuantity] = useState(1);
  const increment = () => {
    const qnt = quantity + 1;
    setQuantity(qnt);
    return qnt;
  };
  const decrease = () => {
    if (quantity > 1) {
      const qnt = quantity - 1;
      setQuantity(qnt);
      return qnt;
    }
  };
  console.log(product.categories);
  const setQnt = (e) => {
    set_Qnt({
      ...Qnt,

      qnt: e,
    });
    setCart({
      productId: { ...product },
      ...product,
      ...Cart,
      quantity: e,
    });
    setValue({ ...product, quantity: e });
  };
  const addHandle = async () => {
    if (list_Color && list_Size) toCart(Cart, product);
  };

  return (
    <>
      <AppNavbar />

      {loadingPro ? (
        <Loading />
      ) : (
        <>
          <div className="modal" tabindex="-1" role="dialog">
            <div className="modal-dialog" role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Modal title</h5>
                  <button
                    type="button"
                    className="close"
                    data-dismiss="modal"
                    aria-label="Close"
                  >
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <p>Modal body text goes here.</p>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-primary">
                    Save changes
                  </button>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default ProDetailes;
