import styled from "styled-components";

const Wrapper = styled.main`
  .loading {
    position: fixed;
    z-index: 3000;
    top: 0;
    left: 0;
    bottom: 0;
    height: 100%;
    background-color: white;
    right: 0;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  }
  .loading h1 {
    color: #941082;
    font-size: 4rem;
    margin: 0;
  }
  .loading .gif {
    width: 30%;
  }
  .loading .gif img {
    height: 100%;
    width: 100%;
  }
`;
export default Wrapper;
