import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";
import { useParams, useNavigate } from "react-router-dom";
import { MDBCol } from "mdbreact";

import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { api, deletePro } from "../rtk/slices/productsSlice";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";

function Products() {
  const { products, error } = useSelector((state) => state.products);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(api());
  }, [dispatch]);
  return (
    <>
      <Container className="mt-5 pt-5">
        {error && <Alert color="warning">Error in Api</Alert>}
        <Row>
          <Col>
            <h1 className="pb-5 text-start">Mange Products</h1>
          </Col>
          <Col>
            <MDBCol md="3">
              <form className="form-inline mt-4 mb-4">
                <input
                  className="form-control form-control-sm ml-3 w-75"
                  type="text"
                  placeholder="Search"
                  aria-label="Search"
                />
              </form>
            </MDBCol>
          </Col>
          <Col className="col-md-2">
            <Link to="/AddProduct">
              <Button variant="primary">Add Product</Button>
            </Link>
          </Col>
        </Row>

        {products.length > 0 && (
          <Table striped bordered hover className="m-3">
            <thead>
              <tr>
                <th>#</th>
                <th>Photo</th>
                <th>First Name</th>
                <th>Category</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>
                    <img
                      src={product.img}
                      style={{ width: "50px", height: "50px" }}
                    />
                  </td>
                  <td>{product.productName}</td>
                  <td>{product.category}</td>
                  <td>{product.price}</td>
                  <td>
                    <Link to={`/Delete/${product.id}`}>
                      <Button
                        className="m-2"
                        variant="danger"
                        onClick={() => {
                          dispatch(deletePro(product));
                        }}
                      >
                        Delete
                      </Button>
                    </Link>

                    <Link to={`/Update/${product.id}`}>
                      <Button value={product.id} variant="success">
                        Edit
                      </Button>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </Container>
    </>
  );
}
export default Products;
