import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { deleteCat } from "../../rtk/slices/categoriesSlice";
import axios from "axios";
import { useEffect, useState } from "react";

import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddCategory from "../add/Add";
import Form from "react-bootstrap/Form";
import { updateCat } from "../../rtk/slices/categoriesSlice";
import { useNavigate, useParams } from "react-router-dom";
import { useRef } from "react";
import { useAppContext } from "../../context/appContext";
import {
  GET_CATEGORIES,
  UPDATE_CATEGORY,
  DELETE_CATEGORY,
  RESET_CAT,
} from "../../rtk/slices/categoriesSlice";

const CategoriesTable = ({ formState, setForm }) => {
  const { categories } = useSelector((state) => state.categories);
  const { deleteItem, UpdateItem, getAllItems, getCategories } =
    useAppContext();
  const dispatch = useDispatch;
  const params = useParams();
  const navigate = useNavigate();
  // const selectedCategory = categories.filter((user) => user._id === params.id);
  // const { categoryName, elementState } = selectedCategory[0];
  const ref = useRef(null);

  const deleteCategory = async (id) => {
    try {
      await deleteItem(id);
      await getCategories();
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };

  const HandleUpdate = async (e) => {
    console.log(e);
    const data = {
      categoryName: e.categoryName,
      elementState: !e.elementState,
      image: e.image,
      _id: e._id,
    };

    UpdateItem(data);
    getCategories();
  };

  return (
    <>
      <Table>
        <thead>
          <tr>
            {/* <th>#</th> */}
            <th>Category Name</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category._id}>
              {/* <td style={{ width: " 20px" }}></td> */}
              <td>{category.categoryName}</td>

              <td>
                {/* <button onClick={() => HandleUpdate(category)}>
                  {" "}
                  <i className="fa-solid fa-eye"></i>
                </button> */}
                <Form>
                  <Link
                  // to={`/dashboard/MangeCategories/UpdateState/${category._id}`}
                  >
                    <Form.Check
                      type="switch"
                      id={category._id}
                      ref={ref}
                      label="Active"
                      name={category.categoryName}
                      checked={category.elementState === true ? true : false}
                      onChange={(e) => HandleUpdate(category)}
                    />
                  </Link>
                </Form>
              </td>
              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton onClick={() => deleteCategory(category._id)} />
                </span>
                <span className="edit-section">
                  <Link
                    to={`/dashboard/MangeCategories/Update/${category._id}`}
                  >
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddCategory formState={formState} setForm={setForm} />
    </>
  );
};

export default CategoriesTable;
