import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import { useDispatch, useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import { deleteCart, clearCart, updateCart } from "../rtk/slices/cartSlice";
import { useState } from "react";
import Form from "react-bootstrap/Form";
import "./Edit.css";
import { Link } from "react-router-dom";

function Cart() {
  const [x, xset] = useState({});
  const cartItems = useSelector((state) => state.cart);

  const dispatch = useDispatch();
  const total = cartItems.reduce(
    (previousScore, currentScore) =>
      (previousScore += currentScore.qnt * parseInt(currentScore.unitPrice)),
    0
  );
  const xx = (cartId) => {
    const select = cartItems.find(
      (item) => item.id.toString() === cartId.toString()
    );
    console.log(select);
    xset(select);
    console.log(x);
    dispatch(updateCart(x));
  };
  const v = () => {
    return (
      <div className="editForm">
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>ProductName</Form.Label>
            <Form.Control type="text" value={x.productName || ""} />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>quantityPerUnit</Form.Label>
            <Form.Control type="text" value={x.quantityPerUnit || ""} />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>unitPrice</Form.Label>
            <Form.Control type="text" value={x.unitPrice || ""} />
          </Form.Group>

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    );
  };
  return (
    <>
      <Container className="p-5">
        <h1>cart</h1>
        <Button variant="primary" onClick={() => dispatch(clearCart())}>
          Clear Cart
        </Button>
        <h3>{total}</h3>
        <Table striped bordered hover className="m-3">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Units</th>
              <th>unitPrice</th>
              <th>action</th>
              <th>qnt</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((cartItem) => (
              <tr key={cartItem.id}>
                <td>{cartItem.id}</td>
                <td>{cartItem.productName}</td>
                <td>{cartItem.quantityPerUnit}</td>
                <td>{cartItem.unitPrice}</td>
                <td>{cartItem.qnt}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => dispatch(deleteCart(cartItem.id))}
                  >
                    Delete
                  </Button>
                  <Button
                    value={cartItem.id}
                    variant="success"
                    onClick={(e) => xx(e.target.value)}
                  >
                    <Link to="/Edit/:id">Edit</Link>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {v()}
      </Container>
    </>
  );
}
export default Cart;
