import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { updateCat } from "../../rtk/slices/categoriesSlice";
import axios from "axios";
import { useAppContext } from "../../context/appContext";

function UpdateCat() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const { categories } = useSelector((state) => state.categories);
  const selectedCategory = categories.filter((user) => user._id === params.id);
  const { categoryName, elementState, image } = selectedCategory[0];
  const [value, setValue] = useState({
    categoryName,
    image,
    elementState,
    _id: params.id,
  });
  const handleImage = (e) => {
    const image = e.target.files[0].name;
    // const array = image.forEach((element) => {
    //   array.push(element.name);
    // });

    setValue({ ...value, image: image });
  };
  console.log(value);
  const { UpdateItem, getAllItems } = useAppContext();

  const HandleUpdate = (e) => {
    e.preventDefault();
    UpdateItem(value);
  };

  return (
    <>
      <div>
        <form
          onSubmit={HandleUpdate}
          className="editForm"
          encType="multipart/form-data"
        >
          <div className="mb-3 text-start">
            <select
              className="input-select m-2"
              onClick={(e) =>
                setValue({ ...value, categories: e.target.value })
              }
            >
              {categories.map((category) => (
                <option value={category.categoryName}>
                  {category.categoryName}
                </option>
              ))}
            </select>
          </div>

          <div className="mb-3 text-start">
            <label>Image</label>
            <input
              type="file"
              className="form-control"
              onChange={(e) => handleImage(e)}
            />
          </div>
          <div className="form-check form-switch">
            <label>State</label>
            <input
              className="form-check-input"
              type="checkbox"
              id="custom-switch"
              checked={value.elementState === true ? true : false}
              onChange={(e) =>
                setValue({ ...value, elementState: !value.elementState })
              }
            />
          </div>
          <Button type="submit" variant="primary">
            Submit
          </Button>
        </form>
      </div>
    </>
  );
}

export default UpdateCat;
