import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
export const apiColors = createAsyncThunk("sizesSlice/apiColors", async () => {
  const res = await fetch("http://localhost:3000/colors");
  const data = await res.json();
  return data;
});

export const insertColor = createAsyncThunk(
  "colorsSlice/insertColor",
  async (colorName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    try {
      const res = await fetch("http://localhost:3000/colors", {
        method: "POST",

        body: JSON.stringify(colorName),

        headers: { "Content-type": "application/json; charset=utf-8" },
      });

      const data = await res.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const updateColor = createAsyncThunk(
  "colorsSlice/updateColor",
  async (colorName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    try {
      const res = await fetch(`http://localhost:3000/colors/${colorName.id}`, {
        method: "PUT",

        body: JSON.stringify(colorName),

        headers: { "Content-type": "application/json; charset=utf-8" },
      });
      const data = await res.json();
      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const deleteColor = createAsyncThunk(
  "colorsSlice/deleteColor",
  async (colorName, thunkApi) => {
    const { rejectWithValue } = thunkApi;
    console.log(colorName);
    try {
      await fetch(`http://localhost:3000/colors/${colorName.id}`, {
        method: "DELETE",

        headers: { "Content-type": "application/json; charset=utf-8" },
      });
      return colorName;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
const colorsSlice = createSlice({
  name: "colorsSlice",
  initialState: [],
  reducers: {
    ADD_Color: (state, action) => {
      state.colors = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(apiColors.fulfilled, (state, action) => {
      return action.payload;
    });
    builder.addCase(insertColor.fulfilled, (state, action) => {
      state.push(action.payload);
    });
    builder.addCase(deleteColor.fulfilled, (state, action) => {
      return (state = state.filter((color) => color.id !== action.payload.id));
    });

    builder.addCase(updateColor.fulfilled, (state, action) => {
      const { id, colorName, elementState } = action.payload;
      const updateState = state.find((color) => color.id === id);
      if (updateState) {
        updateState.colorName = colorName;
        updateState.elementState = elementState;
      }
    });
  },
});

export const { ADD_Color } = colorsSlice.actions;
export default colorsSlice.reducer;
