import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import { useDispatch, useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import { deleteCart, clearCart } from "../rtk/slices/cartSlice";

function Cart() {
  const cartItems = useSelector((state) => state.cart);
  const dispatch = useDispatch();
  const total = cartItems.reduce((tot, item) => {
    tot += item.unitPrice;
  });
  return (
    <>
      <Container className="p-5">
        <h1>cart</h1>
        <Button variant="primary" onClick={() => dispatch(clearCart())}>
          Clear Cart
        </Button>
        <h3>u</h3>
        <Table striped bordered hover className="m-3">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Units</th>
              <th>unitPrice</th>
              <th>action</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((cartItem) => (
              <tr key={cartItem.id}>
                <td>{cartItem.id}</td>
                <td>{cartItem.productName}</td>
                <td>{cartItem.quantityPerUnit}</td>
                <td>{cartItem.unitPrice}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => dispatch(deleteCart(cartItem.id))}
                  >
                    Delete
                  </Button>
                  <Button variant="success">Edit</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    </>
  );
}
export default Cart;
