import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
// import { apiCategories } from "../../rtk/slices/categoriesSlice";
const Categories = () => {
  const categories = useSelector((state) => state.categories);
  const dispatch = useDispatch();
  // useEffect(() => {
  //   dispatch(apiCategories());
  // }, [dispatch]);
  return (
    <>
      {/* <Container className="">
        <h1 className="pb-5">Categories</h1>
        <Row>
          {categories.map((category) => (
            <Col key={category.id} className="col-md-3 p-5">
              <Card style={{ width: "240px", height: "250px" }}>
                <Card.Img
                  variant="top"
                  src={category.img}
                  style={{ height: "100%" }}
                />
                <Card.Body>
                  <Card.Title>{category.categoryName}</Card.Title>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container> */}
      <div class="section">
        <div class="container">
          <div class="row">
            <div class="col-md-4 col-xs-6">
              <div class="shop">
                <div class="shop-img">
                  <img src="./img/shop01.png" alt="" />
                </div>
                <div class="shop-body">
                  <h3>
                    Laptop<br>Collection</br>
                  </h3>
                  <a href="#" class="cta-btn">
                    Shop now <i class="fa fa-arrow-circle-right"></i>
                  </a>
                </div>
              </div>
            </div>

            <div class="col-md-4 col-xs-6">
              <div class="shop">
                <div class="shop-img">
                  <img src="./img/shop03.png" alt="" />
                </div>
                <div class="shop-body">
                  <h3>
                    Accessories<br>Collection</br>
                  </h3>
                  <a href="#" class="cta-btn">
                    Shop now <i class="fa fa-arrow-circle-right"></i>
                  </a>
                </div>
              </div>
            </div>

            <div class="col-md-4 col-xs-6">
              <div class="shop">
                <div class="shop-img">
                  <img src="./img/shop02.png" alt="" />
                </div>
                <div class="shop-body">
                  <h3>
                    Cameras<br>Collection</br>
                  </h3>
                  <a href="#" class="cta-btn">
                    Shop now <i class="fa fa-arrow-circle-right"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Categories;
