import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { updateCat } from "../../rtk/slices/categoriesSlice";

function UpdateCat() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  console.log(params.id);
  const { categories } = useSelector((state) => state.categories);
  console.log(categories);
  const selectedCategory = categories.filter(
    (user) => user.id.toString() === params.id.toString()
  );
  const { categoryName, elementState } = selectedCategory[0];
  const [value, setValue] = useState({
    categoryName,
    // img,
    elementState,
  });
  const HandleUpdate = () => {
    dispatch(
      updateCat({
        id: params.id,
        categoryName: value.categoryName,
        // img: "./img/" + value.img.slice(12),
        elementState: value.elementState,
      })
    );
    navigate("/dashboard/MangeCategories");
  };
  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>ProductName</Form.Label>
          <Form.Control
            type="text"
            value={value.categoryName}
            onChange={(e) =>
              setValue({ ...value, categoryName: e.target.value })
            }
          />
        </Form.Group>

        {/* <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>unitPrice</Form.Label>
          <Form.Control
            type="file"
            onChange={(e) => setValue({ ...value, img: e.target.value })}
          />
        </Form.Group> */}
        <Form.Check
          type="switch"
          id="custom-switch"
          label="Active"
          checked={value.elementState === true ? true : false}
          onClick={(e) =>
            setValue({ ...value, elementState: !value.elementState })
          }
        />
        <Button variant="primary" onClick={() => HandleUpdate()}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default UpdateCat;
