import { useDispatch } from "react-redux";
import React, { useContext } from "react";
import { CLEAR_ALERT, DISPLAY_ALERT } from "../rtk/slices/authSlice";
const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();

  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2500);
  };

  const displayAlert = (e) => {
    dispatch(
      DISPLAY_ALERT({ alertType: "danger", alertText: e.response.data.msg })
    );
    clearAlert();
  };

  return (
    <AppContext.Provider value={{ clearAlert }}>{children}</AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
