import { createSlice } from "@reduxjs/toolkit";

const colorsSlice = createSlice({
  name: "colorsSlice",
  initialState: { colors: [] },
  reducers: {
    GET_COLORS: (state, action) => {
      state.colors = action.payload;
    },
    ADD_COLOR: (state, action) => {
      state.colors.push(...action.payload);
    },
    DELETE_COLOR: (state, action) => {
      state.deletedColor = true;
      state.products.filter((el) => el._id !== action.payload);
    },
    DELETE_RESET_COLOR: (state, action) => {
      state.deletedColor = false;
    },
  },
});

export const { GET_COLORS, ADD_COLOR, DELETE_COLOR, DELETE_RESET_COLOR } =
  colorsSlice.actions;
export default colorsSlice.reducer;
