import React, { useEffect, useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddCategoryForm = ({ setForm }) => {
  const navigate = useNavigate();

  const [data, setData] = useState({
    categoryName: "",
    image: [],
    elementState: false,
  });
  const handleImage = (e) => {
    const image = e.target.files[0].name;
    // const array = image.forEach((element) => {
    //   array.push(element.name);
    // });

    setData({ ...data, image: image });
  };
  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        "/dashboard/MangeCategories/addCategory",
        data
      );
      navigate(`/dashboard/MangeCategories/AddedCategory`);
      window.location.reload();

      setForm(false);
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>

      <div className="mb-3 text-start">
        <label>Category Name</label>
        <input
          type="text"
          className="form-control"
          value={data.categoryName}
          onChange={(e) => setData({ ...data, categoryName: e.target.value })}
        />
      </div>

      <div className="mb-3 text-start">
        <label>Image</label>
        <input
          multiple
          type="file"
          className="form-control"
          onChange={(e) => handleImage(e)}
        />
      </div>

      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Category
        </button>
      </div>
    </form>
  );
};

export default AddCategoryForm;
