import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";
import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { api, deletePro } from "../../rtk/slices/productsSlice";
import { apiCategories } from "../../rtk/slices/categoriesSlice";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import { MDBCol } from "mdbreact";
import DropdownButton from "react-bootstrap/DropdownButton";
import CategoriesTable from "./CategoriesTable";
import ProductsTable from "./ProductsTable";
function Products() {
  const { products, error } = useSelector((state) => state.products);
  const categories = useSelector((state) => state.categories);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(api());
    dispatch(apiCategories());
  }, [dispatch]);
  const [filterVal, setFilter] = useState("");
  const filter = (e) => {
    setFilter(e.target.value);
  };
  const productsList = () => {
    const e = products.filter((e) => e.productName.includes(filterVal));
    return e;
  };
  // const categoriesTable = () => {
  //   const e = categories.filter((e) => e.categoryName.includes(filterVal));
  //   return e;
  // };
  let x = null;
  if (
    window.location.pathname === "/Dashboard/MangeProducts" ||
    window.location.pathname === "/Dashboard/MangeProducts/Delete/"
  ) {
    x = productsList().length > 0 && (
      <Table striped>
        <thead>
          <tr>
            <th>Photo</th>
            <th>First Name</th>
            <th>Category</th>
            <th>Price</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>
                <img
                  src={product.img}
                  style={{ width: "50px", height: "50px" }}
                  alt="Not Found"
                />
              </td>
              <td>{product.productName}</td>
              <td>{product.category}</td>
              <td>{product.price}</td>
              <td>
                <Link to={`/Dashboard/MangeProducts`}>
                  <Button
                    className="m-2"
                    variant="danger"
                    onClick={() => {
                      dispatch(deletePro(product));
                    }}
                  >
                    Delete
                  </Button>
                </Link>

                <Link to={`/Update/${product.id}`}>
                  <Button value={product.id} variant="success">
                    Edit
                  </Button>
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    );
  }
  if (
    window.location.pathname === "/Dashboard/MangeCategories" ||
    window.location.pathname === "/Dashboard/MangeCategories/Delete/"
  ) {
    x = categories.length > 0 && <CategoriesTable />;
  }

  return (
    <>
      {error && <Alert color="warning">Error in Api</Alert>}
      <Row>
        <Col md="2">
          <div className="control-side">
            <div className="admin-avatar">
              <div className="avatar-img">
                <img src="../img/admin.png" alt="NotFound"></img>
              </div>
              <p>Admin Dashboard</p>
            </div>
            <div className="control-list">
              <ul>
                <li>
                  <Link className="nav-link" to="/Dashboard/MangeProducts">
                    Mange Products
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/Dashboard/MangeCategories">
                    Mange Categories
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </Col>
        <Col md="10">
          <div className="d-flex align-items-center justify-content-center">
            <MDBCol md="3">
              <form className="form-inline mt-4 mb-4">
                <input
                  className="form-control form-control-sm ml-3 w-75 input-style"
                  type="text"
                  placeholder="Search"
                  value={filterVal}
                  aria-label="Search"
                  onChange={filter}
                />
              </form>
            </MDBCol>
            <DropdownButton
              id="dropdown-basic-button"
              title={
                window.location.search === "?addedCategory"
                  ? "AddCategory"
                  : "AddProduct"
              }
            >
              <Link to="/AddProduct">Add Product</Link>
              <Link to="/AddCategory">Add Category</Link>
            </DropdownButton>
          </div>

          <div className="table">{x}</div>
        </Col>
      </Row>
    </>
  );
}
export default Products;
