  console.log(product.categories);
  const setQnt = (e) => {
    set_Qnt({
      ...Qnt,

      qnt: e,
    });
    setCart({
      productId: { ...product },
      ...product,
      ...Cart,
      quantity: e,
    });
    setValue({ ...product, quantity: e });
  };
  const addHandle = async () => {
    if (list_Color && list_Size) toCart(Cart, product);
  };
  if (!list_Color && !list_Size);

