import React, { useEffect, useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
// import AlertMsg from "../Alert";
import { useSelector } from "react-redux";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";
import FormRowSelect from "../modal/FormRowSelect";
// import Wrapper from "../../Wrapper/FormRow";
import UpdateProduct from "../update/UpdateProduct";
import { useParams } from "react-router-dom";
import { useDispatch } from "react-redux";

const AddProductForm = () => {
  const dispatch = useDispatch();

  const {
    updateProId,
    name,
    price,
    sizeList,

    selectedColorList,
    selectedSizeList,
    elementState,
    image,
    description,
    selectedCategories,
    isAdd,
    isEdit,
  } = useSelector((state) => state.products);
  const { sizes } = useSelector((state) => state.sizes);
  const { colors } = useSelector((state) => state.colors);
  const { categories } = useSelector((state) => state.categories);
  const { setForm, addItem, UpdateItem } = useAppContext();
  const params = useParams();

  let [data, setData] = useState({
    name,
    description,
    selectedCategories,
    price,
    colorList: [],
    sizeList: [],
    elementState,
    image: [],

    _id: updateProId || params.id,
  });
  const handleImage = (e) => {
    const x = e.target.files;
    const list = [];

    for (let i = 0; i < x.length; i++) {
      list.push(e.target.files[i].name);
    }

    setData({ ...data, image: list });
  };

  const setColor = (selectedItem) => {
    if (selectedItem !== "select") {
      let list = data.selectedColorList;

      let exist = list.find((item) => item === selectedItem);
      if (!exist) {
        list.push(selectedItem);
      }
      setData({ ...data, colorList: list });
    }
  };
  console.log(data.sizeList);
  // const setSize = (selectedItem) => {
  //   // if (selectedItem !== "select") {
  //   let list = data.sizeList;

  //   let exist = list.find((item) => item === selectedItem);
  //   if (!exist) {
  //     console.log("g");
  //     list.push(selectedItem);
  //     console.log(list);

  //     // dispatch(Set_Size_List(data.sizeList));
  //     // getProducts();
  //   }

  //   setData({ ...data, sizeList: list });
  //   // dispatch(Set_Size_List([selectedItem]));
  //   // }
  // };
  const setSize = (itemName, state) => {
    let list = [];
    if (state) {
      list = data.sizeList;
      list.push(itemName);
      setData({ ...data, sizeList: list });
    } else {
      list = data.sizeList.filter((item) => itemName !== item);
      setData({ ...data, sizeList: list });
    }
  };
  const sil = (s) => {
    let filter = selectedSizeList.filter((item) => item !== s);
    console.log(filter);
    setData({ ...data, sizeList: filter });
    dispatch(Set_Size_List(filter));
  };
  console.log(data.sizeList);

  useEffect(() => {
    console.log("f");
    // data.sizeList = selectedSizeList;
    // dispatch(Set_Size_List(data.sizeList));
  }, [data]);
  return (
    <>
      {/* {updateProId ? (
        <UpdateProduct />
      ) : ( */}
      <form
        className="editForm"
        onSubmit={(e) => {
          if (isAdd) {
            return addItem(e, data);
          } else {
            return UpdateItem(e, data);
          }
        }}
      >
        {/* {showAlert && <AlertMsg alertType={alertType} alertText={alertText} />} */}
        <h3>{isEdit ? "Edit Product" : "Add Product"}</h3>
        <span className="delete-section">
          <CloseButton
            onClick={() => {
              setForm(false);
            }}
          />
        </span>
        <FormRow
          type="text"
          name="Name"
          value={data.name}
          handleChange={(e) => setData({ ...data, name: e.target.value })}
        />
        <FormRow
          type="text"
          name="Description"
          value={data.description}
          handleChange={(e) =>
            setData({ ...data, description: e.target.value })
          }
        />
        <FormRow
          type="text"
          name="Price"
          value={data.price}
          handleChange={(e) => setData({ ...data, price: e.target.value })}
        />
        <FormRowSelect
          name="categories"
          labelText="categories"
          // value={data.categoryName}
          handleChange={(e) => {
            setData({ ...data, categories: e.target.value });
          }}
          list={categories}
          x="categories"
          selected={selectedCategories}
        />

        <div className="row mt-2">
          <div className="col-md-6">
            <h5>Sizes</h5>
            {sizes.map((size, index) => (
              <FormRow
                type="checkbox"
                name={size.sizeName}
                value={data.selectedSizeList}
                handleChange={(e) => {
                  // setData({ ...data, sizeList: e.target.value });
                  // setSize(e.target.name);
                  setSize(e.target.name, e.target.checked);
                }}
              />
            ))}
          </div>
          <div className="col-md-6">
            {" "}
            <h5>Colors</h5>
            {colors.map((color, index) => (
              <FormRow
                type="checkbox"
                name={color.colorName}
                // value={data.colorList[index]}
                handleChange={(e) => {
                  // setData({ ...data, colorList: e.target.value });

                  setColor(e.target.name, e.target.checked);
                }}
              />
            ))}
          </div>
        </div>
        <h5>State</h5>
        <FormRow
          type="checkbox"
          name="Active"
          value={data.elementState}
          handleChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
        <FormRow
          type="file"
          name="Image"
          handleChange={(e) => handleImage(e)}
        />

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Add Product
          </button>
        </div>
      </form>
      {/* )} */}
    </>
  );
};

export default AddProductForm;
