import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../rtk/slices/cartSlice";
import AppNavbar from "../secrions/AppNavbar";
import Wrapper from "../../Wrapper/Home";
function Home() {
  const { products } = useSelector((state) => state.products);
  const dispatch = useDispatch();

  return (
    <>
      {/* <Categories/>  */}
      {/* <Slider />  */}
      <AppNavbar />
      <Wrapper>
        <Container className="mt-5 pt-5">
          <h1 className="pb-5">Products</h1>

          <Row className="px-xl-5 pb-3">
            {products.map((product) => (
              <Col
                key={product.id}
                className="col-lg-3 col-md-6 col-sm-12 p-2 product-style"
              >
                <Card className="card-style">
                  <Card.Img
                    variant="top"
                    src={`/img/${product.image}`}
                    style={{ height: "100%", width: "100%" }}
                  />
                  <Card.Body className="card-title">
                    <Card.Text className="text-start">
                      <h5>{product.name}</h5>
                    </Card.Text>
                    <Card.Text className="text-start">
                      <p>{product.price} $</p>
                    </Card.Text>

                    <Card.Footer>
                      <button
                        className="add-button"
                        variant="dark"
                        onClick={() => dispatch(addToCart(product))}
                      >
                        <i className="fas fa-eye  mr-1"></i> View Detail
                      </button>
                      <button
                        className="add-button"
                        variant="dark"
                        onClick={() => dispatch(addToCart(product))}
                      >
                        <i className="fa-solid fa-cart-shopping"></i> Add TO
                        Cart
                      </button>
                    </Card.Footer>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Container>
      </Wrapper>
    </>
  );
}

export default Home;
