import { createSlice } from "@reduxjs/toolkit";

const sizesSlice = createSlice({
  name: "sizesSlice",
  initialState: { sizes: [] },
  reducers: {
    GET_SIZES: (state, action) => {
      state.sizes = action.payload;
    },
    ADD_SIZE: (state, action) => {
      state.sizes.push(...action.payload);
    },
    DELETE_SIZE: (state, action) => {
      state.deletedSize = true;
      state.sizes.filter((el) => el._id !== action.payload);
    },
    DELETE_RESET_SIZE: (state, action) => {
      state.deletedSize = null;
    },
  },
});

export const { GET_SIZES, ADD_SIZE, DELETE_SIZE, DELETE_RESET_SIZE } =
  sizesSlice.actions;
export default sizesSlice.reducer;
