import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";
import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { api, deletePro } from "../../rtk/slices/productsSlice";
import { apiCategories } from "../../rtk/slices/categoriesSlice";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import { MDBCol } from "mdbreact";
import DropdownButton from "react-bootstrap/DropdownButton";
import CategoriesTable from "./CategoriesTable";
import ProductsTable from "./ProductsTable";
function Products() {
  const { products, error } = useSelector((state) => state.products);
  const categories = useSelector((state) => state.categories);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(api());
    dispatch(apiCategories());
  }, [dispatch]);
  const [filterVal, setFilter] = useState("");
  const filter = (e) => {
    setFilter(e.target.value);
  };
  const productsList = () => {
    const e = products.filter((e) => e.productName.includes(filterVal));
    return e;
  };
  // const categoriesTable = () => {
  //   const e = categories.filter((e) => e.categoryName.includes(filterVal));
  //   return e;
  // };
  console.log(categories);
  return (
    <>
      {error && <Alert color="warning">Error in Api</Alert>}
      <Row>
        <Col md="2">
          <div className="control-side">
            <div className="admin-avatar">
              <div className="avatar-img">
                <img src="../img/admin.png" alt="NotFound"></img>
              </div>
              <p>Admin Dashboard</p>
            </div>
            <div className="control-list">
              <ul>
                <li>
                  <Link className="nav-link" to="/Dashboard/MangeProducts">
                    Mange Products
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/Dashboard/MangeCategories">
                    Mange Categories
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </Col>
        <Col md="10">
          <div className="d-flex align-items-center justify-content-center">
            <MDBCol md="3">
              <form className="form-inline mt-4 mb-4">
                <input
                  className="form-control form-control-sm ml-3 w-75 input-style"
                  type="text"
                  placeholder="Search"
                  value={filterVal}
                  aria-label="Search"
                  onChange={filter}
                />
              </form>
            </MDBCol>
            <DropdownButton
              id="dropdown-basic-button"
              title={
                window.location.search === "?addedCategory"
                  ? "AddCategory"
                  : "AddProduct"
              }
            >
              <Link to="/AddProduct">Add Product</Link>
              <Link to="/AddCategory">Add Category</Link>
            </DropdownButton>
          </div>
          <div className="table">
            {window.location.pathname === "/Dashboard/MangeProducts"
              ? // ||         "/Dashboard/MangeProducts/Delete/:id"
                productsList().length > 0 && <ProductsTable />
              : categories.length > 0 && (
                  <CategoriesTable categories={categories} />
                )}
          </div>
        </Col>
      </Row>
    </>
  );
}
export default Products;
