import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import ListGroup from "react-bootstrap/ListGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../add/Add";
import Form from "react-bootstrap/Form";
import { deleteColor } from "../../rtk/slices/colorsSlice";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useAppContext } from "../../context/appContext";

const ColorsTable = ({ formState, setForm }) => {
  const { colors } = useSelector((state) => state.colors);

  const { deleteItem, UpdateItem } = useAppContext();

  const deleteColor = (id) => {
    try {
      deleteItem(id);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  const HandleUpdate = (e) => {
    const data = {
      colorName: e.colorName,
      elementState: !e.elementState,
      _id: e._id,
    };

    UpdateItem(data);
  };

  return (
    <>
      <Table>
        <thead>
          <tr>
            {/* <th>#</th> */}
            <th>Color Name</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {colors.map((color) => (
            <tr key={color._id}>
              {/* <td style={{ width: " 20px" }}>{color._id}</td> */}
              <td>{color.colorName}</td>

              <td>
                {" "}
                <Form>
                  {/* <Link to={`/dashboard/MangeColors/Update/${color._id}`}> */}{" "}
                  <Form.Check
                    type="switch"
                    id={color._id}
                    label="Active"
                    name={color.colorName}
                    checked={color.elementState === true ? true : false}
                    onChange={(e) => HandleUpdate(color)}
                  />
                  {/* </Link> */}
                </Form>
              </td>
              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton onClick={() => deleteColor(color._id)} />
                </span>
                <span className="edit-section">
                  <Link to={`/dashboard/MangeColors/Update/${color._id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default ColorsTable;
