import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState } from "react";
import { updateCart } from "../../rtk/slices/cartSlice";
import axios from "axios";
import "./UpdateCart.css";
function Edit() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  console.log(params.id);
  const cartItems = useSelector((state) => state.cart);
  const selectedCart = cartItems.filter(
    (user) => user._id.toString() === params.id.toString()
  );
  console.log(selectedCart[0].productId);
  const { name, categories, price } = selectedCart[0].productId;
  const [value, setValue] = useState({
    name,
    price,
    categories,
  });
  const HandleEdit = async () => {
    await axios.put(`/dashboard/MangeCategories/${data._id}`, data);

    dispatch(
      updateCart({
        _id: params.id,
        name: value.name,
        price: value.price,
        categories: value.categories,
      })
    );
    navigate("/cart");
  };

  return (
    <div className="editForm">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>ProductName</Form.Label>
          <Form.Control
            type="text"
            value={value.name}
            onChange={(e) => setValue({ ...value, name: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>quantityPerUnit</Form.Label>
          <Form.Control
            type="text"
            value={value.price}
            onChange={(e) => setValue({ ...value, price: e.target.value })}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>unitPrice</Form.Label>
          <Form.Control
            type="text"
            value={value.categories}
            onChange={(e) => setValue({ ...value, categories: e.target.value })}
          />
        </Form.Group>

        <Button variant="primary" onClick={() => HandleEdit()}>
          Submit
        </Button>
      </Form>
    </div>
  );
}

export default Edit;
