import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { deleteCart } from "../rtk/slices/cartSlice";

import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { api, addProduct } from "../rtk/slices/productsSlice";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import { addListener } from "@reduxjs/toolkit";

function Products() {
  const products = useSelector((state) => state.products);
  const dispatch = useDispatch();

  // useEffect(() => {
  //   dispatch(api());
  // }, [dispatch]);

  return (
    <>
      <Container className="mt-5 pt-5">
        <Row>
          <Col>
            <h1 className="pb-5 text-start">Mange Products</h1>
          </Col>
          <Col className="col-md-2">
            <Link to="/AddProduct">
              <Button variant="primary">Add Product</Button>
            </Link>
          </Col>
        </Row>

        {products.length > 0 && (
          <Table striped bordered hover className="m-3">
            <thead>
              <tr>
                <th>#</th>
                <th>Photo</th>
                <th>First Name</th>
                <th>Category</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>
                    <img
                      src={product.img}
                      style={{ width: "50px", height: "50px" }}
                    ></img>
                  </td>
                  <td>{product.productName}</td>
                  <td>{product.category}</td>
                  <td>{product.price}</td>
                  <td>
                    <Button
                      variant="danger"
                      onClick={() => dispatch(deleteCart(product.id))}
                    >
                      Delete
                    </Button>

                    <Link to={`/Edit/${product.id}`}>
                      <Button value={product.id} variant="success">
                        Edit{" "}
                      </Button>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </Container>
    </>
  );
}
export default Products;
