import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import { useDispatch, useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import { deleteCart, clearCart } from "../../rtk/slices/cartSlice";
import { Link } from "react-router-dom";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useEffect, useRef } from "react";
import axios from "axios";
import AppNavbar from "../secrions/AppNavbar";
import { useAppContext } from "../../context/appContext";

function Cart() {
  const { cart } = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  const { deleteFromCart, getCart } = useAppContext();
  // const deleteItem = (id) => {
  //   deleteFromCart(id);
  // };
  const x = useRef(true);

  useEffect(() => {
    if (x.current) {
      x.current = false;
      getCart();
    }
  });
  return (
    <>
      <AppNavbar />

      <Container className="p-5">
        <h1>{cart.length === 0 ? "Empty Cart" : "Cart"}</h1>
        {cart.length > 0 && (
          <Table striped bordered hover className="m-3">
            <thead>
              <tr>
                <th>First Name</th>
                <th>Category</th>
                <th>Price</th>
                <th>qnt</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((cartItem) => (
                <tr key={cartItem._id}>
                  <td>{cartItem.productId.name}</td>
                  <td>{cartItem.productId.categories}</td>
                  <td>{cartItem.productId.price}</td>
                  <td>{cartItem.quantity}</td>
                  <td>
                    <Button
                      variant="danger"
                      className="m-2"
                      onClick={() => deleteFromCart(cartItem._id)}
                    >
                      Delete
                    </Button>

                    {/* <Link to={`/edit/${cartItem._id}`}>
                      <Button value={cartItem._id} variant="success">
                        Edit
                      </Button>
                    </Link> */}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
        {cart.length > 0 && (
          <Row>
            <Col>
              <Button variant="dark" onClick={() => dispatch(clearCart())}>
                Clear Cart
              </Button>
            </Col>
            <Col>
              <h5>Total:</h5>
            </Col>
          </Row>
        )}
      </Container>
    </>
  );
}
export default Cart;
