import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
// import { apiCategories } from "../../rtk/slices/categoriesSlice";
const Categories = () => {
  const { categories } = useSelector((state) => state.categories);

  return (
    <>
      {/* <Container classNameName="">
        <h1 classNameName="pb-5">Categories</h1>
        <Row>
          {categories.map((category) => (
            <Col key={category.id} classNameName="col-md-3 p-5">
              <Card style={{ width: "240px", height: "250px" }}>
                <Card.Img
                  variant="top"
                  src={category.img}
                  style={{ height: "100%" }}
                />
                <Card.Body>
                  <Card.Title>{category.categoryName}</Card.Title>
                </Card.Body>
              </Card>
            </Col>
      
        </Row>
      </Container> */}
      <div className="section">
        <div className="container">
          <div className="row">
            {categories.map((category) => (
              <div className="col-md-4 col-xs-6" key={category._id}>
                <div className="shop">
                  <div className="shop-img">
                    <img src={`./img/${category.image}`} alt="" />
                  </div>
                  <div className="shop-body">
                    <h3>{category.categoryName}</h3>
                    <a href="#" className="cta-btn">
                      Shop now <i className="fa fa-arrow-circle-right"></i>
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default Categories;
