import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";
import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";

import { Link } from "react-router-dom";
import CategoriesTable from "./CategoriesTable";
import ProductsTable from "./ProductsTable";
import SizesTable from "./SizesTable";
import ColorsTable from "./ColorsTable";
import AddProduct from "../add/Add";
import axios from "axios";
import { ADD_PRODUCT } from "../../rtk/slices/productsSlice";
import { ADD_Category } from "../../rtk/slices/categoriesSlice";
import { ADD_Color } from "../../rtk/slices/colorsSlice";

function Dashboard() {
  const { products, error } = useSelector((state) => state.products);
  const { categories } = useSelector((state) => state.categories);
  // const { colors } = useSelector((state) => state.categories.colors);

  const dispatch = useDispatch();
  const [form, setForm] = useState(false);
  const url = window.location.pathname;
  useEffect(() => {
    (async () => {
      try {
        if (url.includes("/MangeProducts")) {
          const res = await axios.get("/dashboard/MangeProducts");
          dispatch(ADD_PRODUCT(res.data.products));
        }
        if (url.includes("/MangeCategories")) {
          const res = await axios.get("/dashboard/MangeCategories");
          dispatch(ADD_Category(res.data.categories));
        }
        if (url.includes("/MangeCategories")) {
          const res = await axios.get("/dashboard/MangeColors");
          dispatch(ADD_Color(res.data.colors));
        }
      } catch (e) {
        console.log(e);
      }
    })();
  }, [url, dispatch]);

  return (
    <>
      {error && <Alert color="warning">Error in Api</Alert>}
      <Row>
        <Col md="2" className="p-0">
          <div className="control-side ">
            <div className="admin-avatar">
              <div className="logo">
                <i className="fa-solid fa-d"></i>
              </div>
              <h5>Diwan Shop</h5>
            </div>
            <div className="control-list">
              <ul>
                <Link className="nav-link" to="/dashboard/MangeProducts">
                  <li>
                    <i className="fa-solid fa-bag-shopping"></i>
                    <span> Mange Products</span>
                  </li>
                </Link>

                <Link className="nav-link" to="/dashboard/MangeCategories">
                  <li>
                    {" "}
                    <i className="fa-solid fa-chart-simple"></i>
                    <span> Mange Categories</span>
                  </li>
                </Link>
                <Link className="nav-link" to="/dashboard/MangeSizes">
                  <li>
                    <i className="fa-solid fa-ruler-horizontal"></i>
                    <span> Mange Size</span>
                  </li>
                </Link>
                <Link className="nav-link" to="/dashboard/MangeColors">
                  <li>
                    <i className="fa-sharp fa-solid fa-palette"></i>{" "}
                    <span>Mange Color</span>
                  </li>
                </Link>
              </ul>
            </div>
          </div>
        </Col>
        <Col md="10" className="p-0">
          <div className="dashboard-nav">
            <i className="fa-solid fa-bars-staggered"></i>
            <Button style={{ width: "115px" }} onClick={() => setForm(true)}>
              {window.location.pathname.includes("/dashboard/MangeProducts") &&
                "AddProduct"}
              {window.location.pathname.includes(
                "/dashboard/MangeCategories"
              ) && "AddCategory"}
              {window.location.pathname.includes("/dashboard/MangeSizes") &&
                "AddSize"}
              {window.location.pathname.includes("/dashboard/MangeColors") &&
                "AddColor"}
            </Button>
          </div>
          <AddProduct formState={form} setForm={setForm} />

          <div className="p-4">
            {window.location.pathname.includes("/dashboard/MangeProducts") &&
              products.length > 0 && (
                <ProductsTable formState={form} setForm={setForm} />
              )}
            {window.location.pathname.includes("/dashboard/MangeCategories") &&
              categories.length > 0 && (
                <CategoriesTable formState={form} setForm={setForm} />
              )}
            {/* {window.location.pathname.includes("/dashboard/MangeSizes") &&
              sizes.length > 0 && (
                <SizesTable formState={form} setForm={setForm} />
              )} */}
            {window.location.pathname.includes("/dashboard/MangeColors") &&
              colors.length > 0 && (
                <ColorsTable formState={form} setForm={setForm} />
              )}
          </div>
        </Col>
      </Row>
    </>
  );
}
export default Dashboard;
