import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useState, useEffect } from "react";
import { updateCat } from "../../rtk/slices/categoriesSlice";
import axios from "axios";
import { useAppContext } from "../../context/appContext";

function UpdateColor() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const { colors, updateColorId } = useSelector((state) => state.colors);
  const selectedColor = colors.filter((user) => user._id === updateColorId);
  console.log(selectedColor[0]);
  const { colorName, elementState } = selectedColor[0];
  const [value, setValue] = useState({
    colorName,
    // img,
    elementState,
    _id: updateColorId,
  });
  console.log(value.elementState);
  useEffect(() => {
    (async () => {
      const res = await axios.get(`/dashboard/MangeColors/${updateColorId}`);
      console.log(res);
      setValue(res.data.size);
      // getsize();
    })();
  }, []);
  const { UpdateItem } = useAppContext();

  const HandleUpdate = (e) => {
    e.preventDefault();
    UpdateItem(value);
  };

  return (
    <>
      <div>
        <form
          onSubmit={HandleUpdate}
          className="editForm"
          encType="multipart/form-data"
        >
          <div className="mb-3 text-start">
            <label>Color Name</label>
            <input
              type="text"
              value={value.colorName}
              className="form-control"
              onChange={(e) =>
                setValue({ ...value, colorName: e.target.value })
              }
            />
          </div>

          <div className="mb-3 text-start">
            <label>Image</label>
            <input
              type="file"
              className="form-control"
              onChange={(e) => setValue({ ...value, img: e.target.value })}
            />
          </div>
          <div className="form-check form-switch">
            <label>State</label>
            <input
              className="form-check-input"
              type="checkbox"
              id="custom-switch"
              checked={value.elementState === true ? true : false}
              onChange={(e) =>
                setValue({ ...value, elementState: !value.elementState })
              }
            />
          </div>
          <Button type="submit" variant="primary">
            Submit
          </Button>
        </form>
      </div>
    </>
  );
}

export default UpdateColor;
