import { useDispatch, useSelector } from "react-redux";
import React, { useContext } from "react";
import { useEffect, useState } from "react";
import { DELETE_PRODUCT, DELETE_RESET } from ".././rtk/slices/productsSlice";

import {
  CLEAR_ALERT,
  DISPLAY_ALERT,
  REGISTER_SUCCESS,
} from "../rtk/slices/authSlice";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { GET_PRODUCTS } from ".././rtk/slices/productsSlice";
import { GET_CATEGORIES } from ".././rtk/slices/categoriesSlice";
import { GET_COLORS } from ".././rtk/slices/colorsSlice";
import { GET_SIZES } from ".././rtk/slices/sizesSlice";

const AppContext = React.createContext();

const AppProvider = ({ children }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { products, error, deleted } = useSelector((state) => state.products);

  const { categories } = useSelector((state) => state.categories);
  const { colors } = useSelector((state) => state.colors);
  const { sizes } = useSelector((state) => state.sizes);
  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2000);
  };

  const displayAlert = (msg) => {
    dispatch(DISPLAY_ALERT({ alertText: msg }));
    clearAlert();
  };
  const successRegister = (user) => {
    dispatch(REGISTER_SUCCESS({ user: user }));
    clearAlert();

    setTimeout(() => {
      navigate("/login");
    }, 2000);
  };

  const getAllItems = async () => {
    const products = await axios.get("/dashboard/MangeProducts");
    dispatch(GET_PRODUCTS(products.data.products));
    const categories = await axios.get("/dashboard/MangeCategories");
    dispatch(GET_CATEGORIES(categories.data.categories));

    const colors = await axios.get("/dashboard/MangeColors");
    dispatch(GET_COLORS(colors.data.colors));

    const sizes = await axios.get("/dashboard/MangeSizes");
    dispatch(GET_SIZES(sizes.data.sizes));
  };

  const deleteItem = async (id) => {
    await axios.delete(`/dashboard/MangeProducts/Delete/${id}`);
    dispatch(DELETE_PRODUCT(id));
  };
  const deleteReset = async () => {
    dispatch(DELETE_RESET());
  };

  return (
    <AppContext.Provider
      value={{
        displayAlert,
        successRegister,
        getAllItems,
        deleteItem,
        deleteReset,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

const useAppContext = () => {
  return useContext(AppContext);
};

export { useAppContext, AppProvider };
