import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import FormRow from "../modal/FormRow";

const AddSizeForm = () => {
  const { setForm, addSize } = useAppContext();
  const [data, setData] = useState({
    sizeName: "",
    elementState: false,
  });

  return (
    <form
      className="editForm"
      onSubmit={(e) => addSize(e, data)}
      encType="multipart/form-data"
    >
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <FormRow
        type="text"
        name="Size Name"
        value={data.sizeName}
        handleChange={(e) => setData({ ...data, sizeName: e.target.value })}
      />
      <h5>State</h5>
      <FormRow
        type="checkbox"
        name="Active"
        value={data.elementState}
        handleChange={(e) =>
          setData({ ...data, elementState: !data.elementState })
        }
      />

      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Size
        </button>
      </div>
    </form>
  );
};

export default AddSizeForm;
