import React, { useContext, useState } from "react";
import style from "./RegisterScreen.module.css";
import authApi from "../../api/auth";
import axios from "axios";
import UserContext from "../../context/userContext";
import { DISPLAY_ALERT, SETUP_USER_SUCCESS } from "../../rtk/slices/authSlice";

function RegisterScreen(props) {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
  });
  const { user, setUser } = useContext(UserContext);
  console.log(user);
  const fetchData = async (e) => {
    e.preventDefault();
    if (
      !data.email ||
      !data.password ||
      !data.fname ||
      !data.lname ||
      (!data.email && !data.password && !data.fname && !data.lname)
    ) {
      dispatch(DISPLAY_ALERT());
    }
    try {
      const res = await axios.post("/register", data);
      console.log(res.data);
      authApi.setToken(res.data.token);
    } catch (e) {
      alert(e.response.data.msg);
      console.log(e.response.data.msg);
    }
  };

  return (
    <>
      <form className={style.registerform} onSubmit={fetchData}>
        <h3>Sign Up</h3>

        <div className="mb-3 text-start">
          <label>First name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setData({ ...data, fname: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Last name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setData({ ...data, lname: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Sign Up
          </button>
        </div>
        <p className="forgot-password text-right">
          Already registered <a href="/sign-in">sign in?</a>
        </p>
      </form>
    </>
  );
}

export default RegisterScreen;
