import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";
import { ADD_COLOR } from "../../rtk/slices/colorsSlice";
import { useDispatch } from "react-redux";
import { useAppContext } from "../../context/appContext";

const AddColorForm = () => {
  const dispatch = useDispatch();
  const { setForm } = useAppContext();

  const [data, setData] = useState({
    colorName: "",
    elementState: false,
  });

  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeColors", data);

      dispatch(ADD_COLOR([res.data.color]));
      setForm(false);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <div className="mb-3 text-start">
        <label>Color Name</label>
        <input
          type="text"
          className="form-control"
          value={data.colorName}
          onChange={(e) => setData({ ...data, colorName: e.target.value })}
        />
      </div>
      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          value={data.elementState}
          onChange={(e) =>
            setData({ ...data, elementState: !data.elementState })
          }
        />
      </div>

      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Color
        </button>
      </div>
    </form>
  );
};

export default AddColorForm;
