import Button from "react-bootstrap/Button";

import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { deleteCat } from "../../rtk/slices/categoriesSlice";
import ListGroup from "react-bootstrap/ListGroup";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../AddProduct";
import Form from "react-bootstrap/Form";
import { useParams, useNavigate } from "react-router-dom";

const ColorsTable = ({ formState, setForm }) => {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const colors = useSelector((state) => state.colors);
  const selectedColor = colors.filter(
    (user) => user.id.toString() === params.id.toString()
  );
  const { colorName, state } = selectedColor[0];

  const [value, setValue] = useState({
    colorName,
    state,
  });
  const HandleUpdate = () => {
    dispatch(
      updateColor({
        id: params.id,
        colorName: value.colorName,
        state: value.state,
      })
    );
    navigate("/Dashboard/MangeColors");
  };

  return (
    <>
      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Color Name</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {colors.map((color) => (
            <tr key={color.id}>
              <td style={{ width: " 20px" }}>{color.id}</td>
              <td>{color.colorName}</td>

              <td>
                {" "}
                <Form>
                  <Form.Check
                    type="switch"
                    id="custom-switch"
                    label="Active"
                    checked={color.state === true ? true : false}
                    onClick={(e) => setValue({ ...value, state: !value.state })}
                  />
                </Form>
              </td>
              <td>
                {" "}
                <span className="delete-section">
                  <CloseButton
                    onClick={() => {
                      dispatch(deleteCat(color));
                    }}
                  />
                </span>
                <span className="edit-section">
                  <Link to={`/Dashboard/MangeColors/Update/${color.id}`}>
                    <i className="fa-regular fa-pen-to-square"></i>
                  </Link>
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <AddProduct formState={formState} setForm={setForm} />
    </>
  );
};

export default ColorsTable;
