import { useDispatch } from "react-redux";
import React, { useContext } from "react";
import {
  DISPLAY_ALERT,
  REGISTER_SUCCESS,
  CLEAR_ALERT,
} from "../rtk/slices/authSlice";
const AppContext = React.createContext();
const AppProvider = ({ children }) => {
  const dispatch = useDispatch();

  const clearAlert = () => {
    setTimeout(() => dispatch(CLEAR_ALERT()), 2500);
  };

  return (
    <AppContext.Provider value={clearAlert}>{children}</AppContext.Provider>
  );
};

export { AppProvider };
