import { createSlice } from "@reduxjs/toolkit";
const cartSlice = createSlice({
  name: "cartSlice",
  initialState: { cart: [] },
  reducers: {
    GET_CART: (state, action) => {
      state.cart = action.payload;
      console.log(action.payload);
    },
    addToCart: (state, action) => {
      console.log(action.payload);
      // const finded = state.find(
      //   (product) => product._id === action.payload._id
      // );
      state.cart.push(action.payload);

      // if (finded) {
      //   console.log(finded.productName);
      //   finded.qnt += action.payload.qnt || 1;
      // } else {
      //   console.log(action.payload);
      //   if (action.payload.qnt) {
      //     const cart = { ...action.payload };
      //     state.push(cart);
      //   } else {
      //     const cart = { ...action.payload, qnt: 1 };
      //     state.push(cart);
      //   }
      // }
    },
    deleteCart: (state, action) => {
      const filter = state.cart.filter((el) => el._id !== action.payload);
      // return filter;
    },
    clearCart: (state, action) => {
      return (state.cart = []);
    },
    updateCart: (state, action) => {
      console.log(action.payload);
      const { _id, name, price, categories } = action.payload;
      const updateState = state.cart.find(
        (product) => product._id.toString() === _id.toString()
      );
      if (updateState) {
        updateState.name = name;
        updateState.price = price;
        updateState.categories = categories;
      }
    },
  },
});

export const { addToCart, deleteCart, clearCart, updateCart, GET_CART } =
  cartSlice.actions;
export default cartSlice.reducer;
