import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import { useDispatch, useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import { deleteCart, clearCart, updateCart } from "../rtk/slices/cartSlice";
import { useState } from "react";
import "./Edit.css";
import { Link } from "react-router-dom";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
function Cart() {
  const [value, valueSet] = useState({});
  const cartItems = useSelector((state) => state.cart);

  const dispatch = useDispatch();
  const total = cartItems.reduce(
    (previousScore, currentScore) =>
      (previousScore += currentScore.qnt * parseInt(currentScore.price)),
    0
  );
  const xx = (cartId) => {
    const select = cartItems.find(
      (item) => item.id.toString() === cartId.toString()
    );
    console.log(select);
    valueSet(select);
    dispatch(updateCart(value));
  };

  return (
    <>
      <Container className="p-5">
        cartItems &&
        <h1>cart</h1>
        <Table striped bordered hover className="m-3">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Category</th>
              <th>Price</th>
              <th>qnt</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((cartItem) => (
              <tr key={cartItem.id}>
                <td>{cartItem.id}</td>
                <td>{cartItem.productName}</td>
                <td>{cartItem.category}</td>
                <td>{cartItem.price}</td>
                <td>{cartItem.qnt}</td>
                <td>
                  <Button
                    variant="danger"
                    onClick={() => dispatch(deleteCart(cartItem.id))}
                  >
                    Delete
                  </Button>
                  <Button
                    value={cartItem.id}
                    variant="success"
                    // onClick={(e) => xx(e.target.value)}
                    // onClick={() => dispatch(updateCart(cartItem))}
                  >
                    <Link to={`/Edit/${cartItem.id}`}>Edit</Link>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <Row>
          <Col>
            {" "}
            <Button variant="primary" onClick={() => dispatch(clearCart())}>
              Clear Cart
            </Button>
          </Col>
          <Col>
            <h5>Total:{total}</h5>
          </Col>
        </Row>
      </Container>
    </>
  );
}
export default Cart;
