import "./Dashboard.css";
import { useSelector } from "react-redux";
// import { deletePro } from "../../rtk/slices/productsSlice";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import AddProduct from "../add/Add";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CloseButton from "react-bootstrap/CloseButton";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { DELETE_PRODUCT } from "../../rtk/slices/productsSlice";
import { useDispatch } from "react-redux";
import { GET_PRODUCTS } from "../../rtk/slices/productsSlice";

const ProductsTable = ({ formState, setForm }) => {
  const params = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { products } = useSelector((state) => state.products);
  console.log(products.length);
  const deleteProduct = async (id) => {
    try {
      const res = await axios.post(`/dashboard/MangeProducts/Delete/${id}`);

      // navigate(`/dashboard/MangeProducts/Delete/${id}`);
      // window.location.reload();
      console.log(id);
      dispatch(DELETE_PRODUCT(id));
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };

  return (
    <>
      <Row>
        {products.map((product) => (
          <Col className=" col-md-4 mb-4 " key={product._id}>
            <Card className="col-style ">
              <span className="delete-section">
                <CloseButton onClick={() => deleteProduct(product._id)} />
              </span>
              <span className="edit-section">
                <Link to={`/dashboard/MangeProducts/Update/${product._id}`}>
                  <i className="fa-regular fa-pen-to-square"></i>
                </Link>
              </span>

              <Row className="d-flex align-items-center">
                <Col md={4}>
                  <Card.Img variant="top" src={`/img/${product.image}`} />
                </Col>
                <Col md={8}>
                  <Card.Title className="text-start">
                    <h2>{product.name}</h2>
                  </Card.Title>
                  <Card.Subtitle className="mt-3 text-muted text-start">
                    {product.price} $
                  </Card.Subtitle>
                </Col>
              </Row>
              <Row>
                <Col>
                  <Card.Body className="mt-3">
                    <Card.Text>{product.description}</Card.Text>
                  </Card.Body>
                </Col>
              </Row>
            </Card>
            <AddProduct formState={formState} setForm={setForm} />
          </Col>
        ))}
      </Row>
    </>
  );
};

export default ProductsTable;
