import { useSelector } from "react-redux";
import AppNavbar from "../secrions/AppNavbar";
import Product from "./Products";
import Categories from "./Categories";
import { useEffect, useState } from "react";
import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Loading from "../Loading";

function Home() {
  const { products } = useSelector((state) => state.products);
  const { loadingColor } = useSelector((state) => state.colors);

  const params = useParams();
  useEffect(() => {
    (async () => {
      await axios.get(`/category/${params.name}`);
    })();
  }, [params.name]);
  if (loadingColor) return <Loading />;

  const [value, setValue] = useState("");

  const filterBy = (e) => {
    return products.filter((e) => e.name.includes(value));
  };
  return (
    <>
      {/* <Slider />  */}

      <AppNavbar />
      <Categories />
      <Product />
    </>
  );
}

export default Home;
