import React, { useState } from "react";
import CloseButton from "react-bootstrap/CloseButton";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddColorForm = ({ setForm }) => {
  const navigate = useNavigate();

  const [data, setData] = useState({
    colorName: "",
  });

  const fetchData = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("/dashboard/MangeColors/addColor", data);

      navigate(`/dashboard/MangeColors/AddedColor`);

      window.location.reload();
      setForm(false);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };
  return (
    <form onSubmit={fetchData} encType="multipart/form-data">
      <span className="delete-section">
        <CloseButton
          onClick={() => {
            setForm(false);
          }}
        />
      </span>
      <div className="mb-3 text-start">
        <label>Color Name</label>
        <input
          type="text"
          className="form-control"
          value={data.colorName}
          onChange={(e) => setData({ ...data, colorName: e.target.value })}
        />
      </div>
      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          onChange={(e) => setData({ ...data, elementState: e.target.value })}
        />
      </div>{" "}
      <div className="mb-3 form-check form-switch">
        <label>State</label>

        <input
          type="checkbox"
          id="custom-switch"
          label="Active"
          className="form-check-input"
          onChange={(e) => setData({ ...data, elementState: e.target.value })}
        />
      </div>
      <div className="d-grid">
        <button type="submit" className="btn btn-primary">
          Add Color
        </button>
      </div>
    </form>
  );
};

export default AddColorForm;
