import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { Alert } from "reactstrap";
import "./RegisterScreen.css";
import axios from "axios";
import authApi from "../../api/auth";
import { DISPLAY_ALERT } from "../../rtk/slices/authSlice";
function LoginScreen(props) {
  const { showAlert } = useSelector((state) => state.auth);

  const [data, setData] = useState({
    email: "",
    password: "",
  });
  const dispatch = useDispatch();
  const fetchData = async (e) => {
    if (!data.email || !data.password) {
      dispatch(DISPLAY_ALERT());
    }
    e.preventDefault();
    try {
      const res = await axios.post("/login", data);
      // const data = await res.json();
      console.log(res.data);
      authApi.setToken(res.data);
    } catch (e) {
      console.log(e.response.data.msg);
    }
  };

  return (
    <>
      <form onSubmit={fetchData}>
        <h3>Log In</h3>
        {showAlert && (
          <Alert color="danger">This is a primary alert â€” check it out!</Alert>
        )}
        <div className="mb-3 text-start">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            onChange={(e) => setData({ ...data, email: e.target.value })}
          />
        </div>

        <div className="mb-3 text-start">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => setData({ ...data, password: e.target.value })}
          />
        </div>

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Log In
          </button>
        </div>
        <p className="forgot-password text-right">
          <a href="/sign-in">Reset password?</a>
        </p>
      </form>
    </>
  );
}

export default LoginScreen;
