import Wrapper from "../../Wrapper/Dashboard";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import CloseButton from "react-bootstrap/CloseButton";
import AddProduct from "../add/Add";
import Form from "react-bootstrap/Form";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../../context/appContext";
import UpdateSize from "../update/UpdateSize";

const SizesTable = ({ formState, setForm }) => {
  const { sizes, updateSizeId } = useSelector((state) => state.sizes);
  const navigate = useNavigate();

  const { deleteItem, UpdateItem, setUpdateSize } = useAppContext();

  const HandleUpdate = (e, size) => {
    const data = {
      sizeName: size.sizeName,
      elementState: !size.elementState,
      _id: size._id,
    };

    UpdateItem(e, data);
  };
  return (
    <>
      <Wrapper>
        {updateSizeId ? (
          <UpdateSize />
        ) : (
          <>
            <Table>
              <thead>
                <tr>
                  <th>SizeName</th>
                  <th>State</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {sizes.map((size) => (
                  <tr key={size._id}>
                    <td>{size.sizeName}</td>
                    <td>
                      {" "}
                      <Form>
                        <Form.Check
                          type="switch"
                          id={size._id}
                          label="Active"
                          name={size.sizeName}
                          checked={size.elementState === true ? true : false}
                          onChange={(e) => HandleUpdate(e, size)}
                        />
                      </Form>
                    </td>

                    <td>
                      {" "}
                      <span className="delete-section">
                        <CloseButton onClick={() => deleteItem(size._id)} />
                      </span>
                      <span className="edit-section">
                        <Link
                          to={`/dashboard/MangeSizes`}
                          onClick={() => setUpdateSize(size)}
                        >
                          <i className="fa-regular fa-pen-to-square"></i>
                        </Link>
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        )}
        <AddProduct formState={formState} setForm={setForm} />
      </Wrapper>
    </>
  );
};

export default SizesTable;
