import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { useEffect, useState } from "react";
import { updateCat } from "../../rtk/slices/categoriesSlice";
import axios from "axios";
import { useAppContext } from "../../context/appContext";

function UpdateSize() {
  const dispatch = useDispatch();
  const params = useParams();
  const navigate = useNavigate();
  const { sizes } = useSelector((state) => state.sizes);
  const selectedSize = sizes.filter((user) => user._id === params.id);
  const { sizeName, elementState } = selectedSize[0];
  const [value, setValue] = useState({
    sizeName,
    // img,
    elementState,
    _id: params.id,
  });
  console.log(value.elementState);
  const { UpdateItem, getsizes } = useAppContext();
  useEffect(() => {
    (async () => {
      const res = await axios.get(`/dashboard/MangeSizes/Update/${params.id}`);
      console.log(res);
      setValue(res.data.size);
      getsizes();
    })();
  }, []);
  const HandleUpdate = (e) => {
    e.preventDefault();
    UpdateItem(value);
  };

  return (
    <>
      <div>
        <form
          onSubmit={HandleUpdate}
          className="editForm"
          encType="multipart/form-data"
        >
          <div className="mb-3 text-start">
            <label>Size Name</label>
            <input
              type="text"
              value={value.sizeName}
              className="form-control"
              onChange={(e) => setValue({ ...value, sizeName: e.target.value })}
            />
          </div>

          <div className="mb-3 text-start">
            <label>Image</label>
            <input
              type="file"
              className="form-control"
              onChange={(e) => setValue({ ...value, img: e.target.value })}
            />
          </div>
          <div className="form-check form-switch">
            <label>State</label>
            <input
              className="form-check-input"
              type="checkbox"
              id="custom-switch"
              checked={value.elementState === true ? true : false}
              onChange={(e) =>
                setValue({ ...value, elementState: !value.elementState })
              }
            />
          </div>
          <Button type="submit" variant="primary">
            Submit
          </Button>
        </form>
      </div>
    </>
  );
}

export default UpdateSize;
