import { createSlice } from "@reduxjs/toolkit";
const authSlice = createSlice({
  name: "authSlice",
  initialState: {
    user: [],
    loading: false,
    showAlert: false,
    alertType: "success",
    alertText: "",
    auth: false,
  },
  reducers: {
    DISPLAY_ALERT: (state, action) => {
      state.showAlert = true;
      state.alertType = action.payload.alertType || "danger";
      state.alertText =
        action.payload.alertText || "Please provide all values!";
    },
    SETUP_USER_SUCCESS: (state, action) => {
      state.showAlert = true;
      state.user = action.payload.user;
      state.alertType = "success";
      state.alertText = "Login Successful! Redirecting...";

      state.auth = true;
    },
    SET_USER_lOGOUT: (state, action) => {
      state.showAlert = false;
      state.user = action.payload.user;
      state.auth = false;
    },
  },
});

export const { DISPLAY_ALERT, SETUP_USER_SUCCESS, SET_USER_lOGOUT } =
  authSlice.actions;
export default authSlice.reducer;
